import React from 'react';
import {useState} from 'react';
import { Validation } from "../Validation/Validation.js";
import styles from '../Form/StyledForm.module.css';



export const Form = ({login}) => {

    const [userData, setUserData] = useState({
        email: "",
        password: ""
    })
    const [errors, setErrors] = useState({
        email: "",
        password: ""
    })


    const handleChange = (event) => {
        const property = event.target.name;
        const value = event.target.value;

        setUserData({...userData, [property]: value});
        Validation({...userData, [property]: value}, setErrors, errors);
    };



    const handleSubmit = (event) => {
        event.preventDefault();
        login(userData); 
    }

    return (
        <div className={FormContainer}>
          <form onSubmit={handleSubmit}>
            <div>
              <h1 className={StyledTitle}>Sign in to peep</h1>
              <label htmlFor='email'>Email:</label>
              <input
                type='text'
                name='email'
                value={userData.email}
                onChange={handleChange}
              />
              <div>
                <span className={StyledErrors}>{errors.email}</span>
              </div>

              <label htmlFor='password'>Password:</label>
              <input
                type='password'
                name='password'
                value={userData.password}
                onChange={handleChange}
              />
              <div>
                <span className={StyledErrors}>{errors.password}</span>
              </div>
            </div>
            <div className={Button}>
              <button type='submit'>Submit</button>
            </div>
          </form>
        </div>
      );
}
