//import React from "react";
import { useState } from "react";
import styles from "./StyledSearch.module.css";


export default function SearchBar({onSearch, onRandom}) {

  const [id, setId] = useState("");

  const handleChange = (event) => {
    setId(event.target.value);
  };

  const randomCharacter = () => {
    const randomId = Math.floor(Math.random() * 826) + 1;

    axios(`https://rickandmortyapi.com/api/character/${randomId}`)
      .then(({ data }) => {
        if (data.name) {
          const characterExists = characters.includes(data);

          if (characterExists) {
            alert("Este personaje ya se encuentra en la lista.");
          } else {
            setCharacters((characters) => [...characters, data]);
          }
        } else {
          alert(`¡No hay personajes con el ID proporcionado!`);
        }
      })
      .catch((error) => {
        alert(
          `Ocurrió un error al obtener los datos de la API. Por favor, intenta nuevamente más tarde.`
        );
        console.error(error);
      });
  };


   return (
    <div>
    <div className={styles.StyledSearch}> 
      <nav className={styles.StyledNav}>
        <input
          type='search'
          placeholder='Add an ID'
          onChange={handleChange}
        />

          <button className={styles.NavButton} onClick={() => onSearch(id)}>Find Character</button>

          <button className={styles.NavButton} onClick={randomCharacter}>Random</button>

      </nav>
    </div>
  </div>
    );
  };
